package tp.java.imperatif;

public class TpJava02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int test = 1234;	
		float test2 = test;
		
		System.out.println("premier test\n");
		
		System.out.println("variable 1 : " + test);
		System.out.println("variable 2 : " + test2);
		
		System.out.println("\ndeuxième test\n");
		
		int test3 = 123456789;
		float test4 = test3;
		
		System.out.println("variable 1 : " + test3);
		System.out.println("variable 2 : " + test4);
		
		System.out.println("\ntroisième test\n");
		
		float x = 15;
		float z = 0;
		float y = -123;
		float a = x/z;
		float b = y/z;
		float c = a/b;
		
		System.out.println("x : " + x);
		System.out.println("z : " + z);
		System.out.println("y : " + y);
		System.out.println("a = x/z : " + a);
		System.out.println("b = y/z : " + b);
		System.out.println("a/b : " + c);
		

	}

}
